{
  "element": "body",
  "style": "background-color: #FF7979; background-image: url('./static/images/bg-intro-desktop.png'); background-repeat: repeat; box-sizing: border-box; display: flex; flex-direction: column; font-family: 'Poppins', serif; justify-content: center; margin: 0; min-height: 100dvh; padding: 24px;",
  "mediaQueries": [
    {
      "query": "max-width: 1024px",
      "style": "background-image: url('./static/images/bg-intro-mobile.png'); justify-content: center; padding: 88px 24px 68px 24px;"
    }
  ],
  "children": [
    {
      "element": "main",
      "style": "display: flex; gap: 32px; justify-content: space-between; margin-inline: auto; max-width: 1110px; min-height: 558px; width: 100%;",
      "mediaQueries": [
        {
          "query": "max-width: 1024px",
          "style": "flex-direction: column; gap: 64px; max-width: fit-content;"
        }
      ],
      "children": [
        {
          "element": "section",
          "style": "align-items: start; display: flex; flex-direction: column; gap: 11px; justify-content: center; max-width: 525px; text-align: start; width: 100%;",
          "mediaQueries": [
            {
              "query": "max-width: 1024px",
              "style": "align-items: center; text-align: center;"
            }
          ],
          "children": [
            {
              "element": "h1",
              "text": "cwrapGlobal[section.title]",
              "style": "color: white; font-size: 50px; font-weight: bold; letter-spacing: -0.52px; line-height: 55px; margin: 0;",
              "mediaQueries": [
                {
                  "query": "max-width: 640px",
                  "style": "font-size: 28px; letter-spacing: -0.29px; line-height: 36px;"
                }
              ]
            },
            {
              "element": "p",
              "text": "cwrapGlobal[section.description]",
              "style": "color: white; font-size: 16px; font-weight: 500; line-height: 26px;"
            }
          ]
        },
        {
          "element": "div",
          "style": "display: flex; flex-direction: column; gap: 24px; max-width: 540px; width: 100%;",
          "children": [
            {
              "element": "div",
              "text": "Try it free 7 days cwrapSpan",
              "style": "align-items: center; background-color: #5E54A4; border-radius: 10px; box-shadow: 0 8px 1px rgba(0, 0, 0, 0.25); color: white; display: flex; font-size: 15px; font-weight: bold; justify-content: center; letter-spacing: 0.27px; line-height: 26px; min-height: 60px;",
              "mediaQueries": [
                {
                  "query": "max-width: 640px",
                  "style": "align-content: center; display: block; min-height: 88px; text-align: center; white-space: pre-wrap;"
                }
              ],
              "children": [
                {
                  "element": "span",
                  "text": "Â then\n$20/mo. thereafter",
                  "style": "font-weight: 400;"
                }
              ]
            },
            {
              "element": "form",
              "attributes": {
                "method": "GET"
              },
              "style": "align-items: center; background-color: white; border-radius: 10px; box-shadow: 0 8px 1px rgba(0, 0, 0, 0.25); box-sizing: border-box; display: flex; flex-direction: column; min-height: 474px; padding: 40px; width: 100%;",
              "mediaQueries": [
                {
                  "query": "max-width: 640px",
                  "style": "padding: 24px;"
                }
              ],
              "children": [
                {
                  "element": "ul",
                  "style": "display: flex; flex-direction: column; gap: 20px; list-style: none; margin: 0; padding: 0; width: 100%;",
                  "mediaQueries": [
                    {
                      "query": "max-width: 640px",
                      "style": "gap: 16px;"
                    }
                  ],
                  "blueprint": {
                    "count": "4",
                    "element": "li",
                    "style": "display: flex; width: 100%;",
                    "children": [
                      {
                        "element": "label",
                        "style": "display: flex; position: relative; width: 100%;",
                        "children": [
                          {
                            "element": "input",
                            "style": "border: 1px solid #DEDEDE; border-radius: 5px; color: #3D3B48; display: flex; font-size: 14px; font-weight: 600; letter-spacing: 0.25px; line-height: 26px; margin: 0; min-height: 56px; outline: none; padding: 0; padding-inline: 32px; width: 100%;",
                            "mediaQueries": [
                              {
                                "query": "max-width: 1024px",
                                "style": "padding-inline: 24px;"
                              },
                              {
                                "query": "max-width: 640px",
                                "style": "padding-inline: 19.5px;"
                              }
                            ],
                            "extend": [
                              {
                                "extension": ":focus",
                                "style": "border-color: #5E54A4;"
                              },
                              {
                                "extension": ":user-invalid",
                                "style": "background: url('static/images/icon-error.svg') no-repeat right 27px top 16px; border: 2px solid #FF7979; margin-bottom: 20px; padding-right: 60px; position: relative; z-index: 2;",
                                "mediaQueries": [
                                  {
                                    "query": "max-width: 1024px",
                                    "style": "padding-right: 44px;"
                                  },
                                  {
                                    "query": "max-width: 640px",
                                    "style": "padding-inline: 39.5px;"
                                  }
                                ]
                              }
                            ],
                            "attributes": {
                              "id": "cwrapArray[first-name,last-name,email,password]",
                              "name": "cwrapArray[first-name,last-name,email,password]",
                              "aria-label": "cwrapArray[First Name,Last Name,Email Address,Password]",
                              "placeholder": "cwrapArray[First Name,Last Name,Email Address,Password]",
                              "autocomplete": "cwrapGlobal[cwrapArray[inputs.0.autocomplete,inputs.1.autocomplete,inputs.2.autocomplete,câwrapOmit]]",
                              "required": "",
                              "pattern": "cwrapGlobal[cwrapArray[inputs.0.pattern,inputs.1.pattern,inputs.2.pattern,cârapOmit]]"
                            }
                          }
                        ]
                      }
                    ]
                  }
                },
                {
                  "element": "button",
                  "style": "background-color: #38B57E; border: 1px solid transparent; border-radius: 5px; cursor: pointer; letter-spacing: 1px; margin-top: 20px; min-height: 56px; outline: none; padding: 0; position: relative; transition: background-color 300ms; width: 100%;",
                  "extend": [
                    {
                      "extension": ":focus",
                      "style": "border: 1px solid #5E54A4;"
                    },
                    {
                      "extension": "> div:nth-of-type(1)",
                      "style": "transition: background-color 300ms;"
                    },
                    {
                      "extension": ":hover > div:nth-of-type(1)",
                      "style": "background-color: #77E2B3;"
                    },
                    {
                      "extension": ":hover",
                      "style": "background-color: #6FCBA2;"
                    }
                  ],
                  "children": [
                    {
                      "element": "div",
                      "style": "align-items: center; background-color: #38CC8B; border-radius: 5px; color: white; display: flex; font-size: 15px; font-weight: 600; justify-content: center; line-height: 26px; min-height: 52px; position: absolute; top: 0; width: 100%;",
                      "children": [
                        {
                          "element": "span",
                          "style": "margin-top: 4px;",
                          "text": "CLAIM YOUR FREE TRIAL"
                        }
                      ]
                    }
                  ]
                },
                {
                  "element": "p",
                  "text": "By clicking the button, you are agreeing to\nour cwrapSpan",
                  "style": "color: #BAB7D4; font-size: 11px; font-weight: 500; line-height: 26px; text-align: center;",
                  "mediaQueries": [
                    {
                      "query": "max-width: 640px",
                      "style": "line-height: 21px; white-space: pre-wrap;"
                    }
                  ],
                  "children": [
                    {
                      "element": "span",
                      "text": "Terms and Services",
                      "style": "color: #FF7979; font-weight: bold;"
                    }
                  ]
                }
              ]
            }
          ]
        }
      ]
    }
  ]
}

